function showAlert(n){const i=`
            <div class="alert alert-${n.class} alert-dismissible fade show">
                ${n.message}
                <button type="button" class="close" data-dismiss="alert" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                </button>
            </div>`,t=$("#alert-div");t.empty();t.append(i);t.fadeIn();setTimeout(()=>{t.fadeOut()},2e3)}function initializeDatetimepicker(n){const t=$(`#${n}`);t.datetimepicker({format:"L"})}