function showAlert(n){const i=n.class==="success"?"check":"alert-triangle",r=`
            <div class="alert alert-${n.class} alert-dismissible fade show">
                <span class="mr-2"><i data-feather="${i}"></i></span><span>${n.message}</span>
                <button type="button" class="close" data-dismiss="alert" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                </button>
            </div>`,t=$("#alert-div");t.empty();t.append(r);t.fadeIn();setTimeout(()=>{t.fadeOut()},2e3)}function initializeDatetimepicker(n){const t=$(`#${n}`);t.datetimepicker({format:"L"})}